   aa_clip &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; core&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;fmtc&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;resample&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;aa_clip&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; w&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; h&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;0&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;-&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;0.5&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;std&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;Transpose&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;17&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;    aa_clip &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; core&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;eedi2&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;EEDI2&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;aa_clip&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; field&lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;1&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; mthresh&lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;10&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; lthresh&lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;20&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; vthresh&lt;span class